    if (!le_pack_UnpackReference( &_msgBufPtr,
                                  &_clientContextPtr ))
    {
        goto error_unpack;
    }

    // Unpack the remaining parameters.
    if (!le_pack_UnpackReference( &_msgBufPtr,
                                               &commandRef ))
    {
        goto error_unpack;
    }

    // Call the registered handler
    if ( _handlerRef_ifgen_le_atServer_AddCmdRegistrationHandler != NULL )
    {
        _handlerRef_ifgen_le_atServer_AddCmdRegistrationHandler(commandRef, contextPtr );
    }
